import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { FaSearch, FaUpload, FaUserPlus } from 'react-icons/fa';

const HeaderContainer = styled.header`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 56px;
  background-color: #202020;
  border-bottom: 1px solid #303030;
  display: flex;
  align-items: center;
  padding: 0 16px;
  z-index: 1000;

  /* Mobile */
  @media (max-width: 768px) {
    height: 48px;
    padding: 0 12px;
  }

  /* TV/Large screens */
  @media (min-width: 1920px) {
    height: 72px;
    padding: 0 40px;
    border-bottom: 2px solid #303030;
  }
  
  @media (min-width: 2560px) {
    height: 88px;
    padding: 0 60px;
    border-bottom: 3px solid #303030;
  }
`;

const Logo = styled(Link)`
  display: flex;
  align-items: center;
  text-decoration: none;
  color: #fff;
  font-size: 20px;
  font-weight: bold;
  margin-right: 40px;
  transition: all 0.2s ease;
  
  &:hover, &:focus {
    color: #ff0000;
    outline: 2px solid #ff0000;
    outline-offset: 4px;
    border-radius: 4px;
  }

  /* Mobile */
  @media (max-width: 768px) {
    font-size: 18px;
    margin-right: 20px;
  }

  /* TV/Large screens */
  @media (min-width: 1920px) {
    font-size: 28px;
    margin-right: 60px;
    
    &:hover, &:focus {
      outline: 3px solid #ff0000;
      outline-offset: 6px;
    }
  }
  
  @media (min-width: 2560px) {
    font-size: 36px;
    margin-right: 80px;
    
    &:hover, &:focus {
      outline: 4px solid #ff0000;
      outline-offset: 8px;
    }
  }
`;

const SearchContainer = styled.div`
  flex: 1;
  max-width: 640px;
  display: flex;
  margin: 0 40px;
`;

const SearchInput = styled.input`
  flex: 1;
  height: 32px;
  padding: 0 12px;
  background-color: #121212;
  border: 1px solid #303030;
  border-radius: 2px 0 0 2px;
  color: #fff;
  font-size: 14px;
  
  &:focus {
    outline: none;
    border-color: #065fd4;
  }
  
  &::placeholder {
    color: #aaa;
  }
`;

const SearchButton = styled.button`
  width: 64px;
  height: 32px;
  background-color: #303030;
  border: 1px solid #303030;
  border-left: none;
  border-radius: 0 2px 2px 0;
  color: #aaa;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  
  &:hover {
    background-color: #3d3d3d;
    color: #fff;
  }
`;

const HeaderActions = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
`;

const ActionButton = styled(Link)`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 16px;
  background-color: transparent;
  border: 1px solid #303030;
  border-radius: 2px;
  color: #aaa;
  text-decoration: none;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background-color: #3d3d3d;
    color: #fff;
    border-color: #aaa;
  }
`;

function Header() {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/?search=${encodeURIComponent(searchQuery.trim())}`);
    }
  };

  return (
    <HeaderContainer>
      <Logo to="/">
        XandTube
      </Logo>
      
      <SearchContainer>
        <form onSubmit={handleSearch} style={{ display: 'flex', flex: 1 }}>
          <SearchInput
            type="text"
            placeholder="Pesquisar"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <SearchButton type="submit">
            <FaSearch />
          </SearchButton>
        </form>
      </SearchContainer>
      
      <HeaderActions>
        <ActionButton to="/upload">
          <FaUpload />
          Upload
        </ActionButton>
        <ActionButton to="/create-channel">
          <FaUserPlus />
          Criar Canal
        </ActionButton>
      </HeaderActions>
    </HeaderContainer>
  );
}

export default Header;
