import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import VideoPlayer from '../components/VideoPlayer';
import { downloadsAPI } from '../services/api';
import { FaSpinner, FaExclamationTriangle, FaArrowLeft } from 'react-icons/fa';

const WatchContainer = styled.div`
  max-width: 100%;
  margin: 0 auto;
  padding: 20px;
  background: #181818;
  min-height: calc(100vh - 56px);

  /* Mobile */
  @media (max-width: 768px) {
    padding: 10px;
    /* Remove top padding to maximize video space */
    padding-top: 0;
  }

  @media (max-width: 480px) {
    padding: 8px;
    padding-top: 0;
  }

  /* TV/Large screens */
  @media (min-width: 1920px) {
    padding: 40px;
    max-width: 1800px;
  }
  
  @media (min-width: 2560px) {
    padding: 60px;
    max-width: 2400px;
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 50vh;
  color: #ffffff;
  
  svg {
    font-size: 48px;
    margin-bottom: 20px;
    animation: spin 1s linear infinite;
  }
  
  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }
`;

const ErrorContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 50vh;
  color: #ffffff;
  text-align: center;
  
  svg {
    font-size: 48px;
    margin-bottom: 20px;
    color: #ff4444;
  }
  
  h2 {
    margin-bottom: 10px;
    font-size: 24px;
  }
  
  p {
    color: #aaa;
    margin-bottom: 20px;
  }
`;

const BackButton = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  background: #3d3d3d;
  border: none;
  color: white;
  padding: 12px 20px;
  border-radius: 8px;
  cursor: pointer;
  font-size: 14px;
  margin-bottom: 20px;
  transition: background-color 0.2s ease;
  
  &:hover {
    background: #555;
  }
  
  svg {
    font-size: 16px;
  }

  /* Mobile */
  @media (max-width: 768px) {
    padding: 16px 24px;
    font-size: 16px;
    margin-bottom: 15px;
    border-radius: 12px;
    
    svg {
      font-size: 20px;
    }
  }

  @media (max-width: 480px) {
    padding: 18px 28px;
    font-size: 18px;
    
    svg {
      font-size: 22px;
    }
  }
`;

const VideoSection = styled.div`
  margin-bottom: 30px;
`;

const VideoDetails = styled.div`
  background: #2d2d2d;
  border-radius: 8px;
  padding: 20px;
  margin-top: 20px;

  /* Mobile */
  @media (max-width: 768px) {
    padding: 16px;
    margin-top: 15px;
    border-radius: 12px;
  }

  @media (max-width: 480px) {
    padding: 14px;
    margin-top: 10px;
    border-radius: 8px;
  }
`;

const VideoTitle = styled.h1`
  color: #ffffff;
  font-size: 24px;
  margin-bottom: 15px;
  line-height: 1.3;

  /* Mobile */
  @media (max-width: 768px) {
    font-size: 20px;
    margin-bottom: 12px;
    line-height: 1.2;
  }

  @media (max-width: 480px) {
    font-size: 18px;
    margin-bottom: 10px;
  }
`;

const VideoInfo = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  margin-bottom: 15px;
  color: #aaa;
  font-size: 14px;

  /* Mobile */
  @media (max-width: 768px) {
    gap: 16px;
    margin-bottom: 12px;
    font-size: 16px;
  }

  @media (max-width: 480px) {
    gap: 12px;
    margin-bottom: 10px;
    font-size: 15px;
    flex-direction: column;
  }
`;

const InfoItem = styled.div`
  display: flex;
  align-items: center;
  gap: 6px;
  
  svg {
    color: #ff0000;
  }

  /* Mobile */
  @media (max-width: 768px) {
    gap: 8px;
    font-weight: 500;
    
    svg {
      font-size: 18px;
    }
  }

  @media (max-width: 480px) {
    gap: 10px;
    
    svg {
      font-size: 20px;
    }
  }
`;

const ChannelInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 15px 0;
  border-top: 1px solid #404040;
  border-bottom: 1px solid #404040;
`;

const ChannelName = styled.h3`
  color: #ffffff;
  font-size: 16px;
  margin: 0;
`;

const Description = styled.div`
  margin-top: 15px;
  color: #ccc;
  line-height: 1.6;
  white-space: pre-wrap;
  max-height: 200px;
  overflow-y: auto;
`;

const WatchPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadVideo = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const response = await downloadsAPI.get(id);
        setVideo(response.data);
      } catch (err) {
        console.error('Erro ao carregar v√≠deo:', err);
        setError('Erro ao carregar o v√≠deo. Verifique se o arquivo ainda existe.');
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      loadVideo();
    }
  }, [id]);

  const handleBack = () => {
    navigate(-1);
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return 'N/A';
    const units = ['B', 'KB', 'MB', 'GB'];
    let size = bytes;
    let unitIndex = 0;
    
    while (size >= 1024 && unitIndex < units.length - 1) {
      size /= 1024;
      unitIndex++;
    }
    
    return `${size.toFixed(1)} ${units[unitIndex]}`;
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <WatchContainer>
        <LoadingContainer>
          <FaSpinner />
          <h2>Carregando v√≠deo...</h2>
          <p>Aguarde enquanto preparamos o player</p>
        </LoadingContainer>
      </WatchContainer>
    );
  }

  if (error || !video) {
    return (
      <WatchContainer>
        <ErrorContainer>
          <FaExclamationTriangle />
          <h2>V√≠deo n√£o encontrado</h2>
          <p>{error || 'O v√≠deo solicitado n√£o foi encontrado ou foi removido.'}</p>
          <BackButton onClick={handleBack}>
            <FaArrowLeft />
            Voltar
          </BackButton>
        </ErrorContainer>
      </WatchContainer>
    );
  }

  return (
    <WatchContainer>
      <BackButton onClick={handleBack}>
        <FaArrowLeft />
        Voltar
      </BackButton>
      
      <VideoSection>
        <VideoPlayer video={video} />
      </VideoSection>
      
      <VideoDetails>
        <VideoTitle>{video.title}</VideoTitle>
        
        <VideoInfo>
          <InfoItem>
            <span>üëÅÔ∏è {video.viewCount?.toLocaleString() || 'N/A'} visualiza√ß√µes</span>
          </InfoItem>
          <InfoItem>
            <span>üìÖ Baixado em {formatDate(video.downloadedAt)}</span>
          </InfoItem>
          <InfoItem>
            <span>üé• {video.resolution || 'N/A'}</span>
          </InfoItem>
          <InfoItem>
            <span>üíæ {formatFileSize(video.fileSize)}</span>
          </InfoItem>
          <InfoItem>
            <span>‚è±Ô∏è {video.duration || 'N/A'}</span>
          </InfoItem>
        </VideoInfo>
        
        <ChannelInfo>
          <ChannelName>{video.channelName || 'Canal Desconhecido'}</ChannelName>
        </ChannelInfo>
        
        {video.description && (
          <Description>
            {video.description}
          </Description>
        )}
      </VideoDetails>
    </WatchContainer>
  );
};

export default WatchPage;
