import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import styled from 'styled-components';
import VideoGrid from '../components/VideoGrid';
import DownloadCard from '../components/DownloadCard';
import { videosAPI, downloadsAPI } from '../services/api';

const HomeContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SearchInfo = styled.div`
  margin-bottom: 20px;
  padding: 16px;
  background-color: #2d2d2d;
  border-radius: 8px;
  border-left: 4px solid #ff0000;
`;

const SearchTitle = styled.h2`
  color: #fff;
  font-size: 20px;
  margin-bottom: 8px;
`;

const SearchQuery = styled.span`
  color: #ff0000;
  font-weight: 500;
`;

const LoadMoreButton = styled.button`
  display: block;
  margin: 40px auto;
  padding: 12px 24px;
  background-color: transparent;
  border: 1px solid #303030;
  border-radius: 2px;
  color: #aaa;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background-color: #3d3d3d;
    color: #fff;
    border-color: #aaa;
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const DownloadsSection = styled.div`
  margin-bottom: 40px;
`;

const SectionTitle = styled.h2`
  color: #fff;
  font-size: 24px;
  margin-bottom: 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const ViewAllLink = styled.a`
  color: #1976d2;
  font-size: 14px;
  text-decoration: none;
  cursor: pointer;
  
  &:hover {
    text-decoration: underline;
  }
`;

const DownloadsGrid = styled.div`
  display: grid;
  gap: 16px;
  grid-template-columns: repeat(auto-fill, minmax(600px, 1fr));
  
  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 40px 20px;
  color: #aaa;
  
  h3 {
    margin-bottom: 8px;
    font-size: 18px;
  }
  
  p {
    margin: 0;
    font-size: 14px;
  }
`;

const LoadingState = styled.div`
  text-align: center;
  padding: 40px 20px;
  color: #aaa;
`;

const ErrorState = styled.div`
  text-align: center;
  padding: 40px 20px;
  color: #ff5722;
  
  h3 {
    margin-bottom: 8px;
    font-size: 18px;
  }
  
  p {
    margin: 0;
    font-size: 14px;
  }
`;

function HomePage() {
  const [searchParams] = useSearchParams();
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hasMore, setHasMore] = useState(false);
  const [loadingMore, setLoadingMore] = useState(false);
  
  // Estados para downloads
  const [downloads, setDownloads] = useState([]);
  const [downloadsLoading, setDownloadsLoading] = useState(false);
  const [downloadsError, setDownloadsError] = useState(null);

  const searchQuery = searchParams.get('search');

  useEffect(() => {
    loadVideos();
    if (!searchQuery) {
      // Só carrega downloads na página inicial
      loadRecentDownloads();
    }
  }, [searchQuery]);

  const loadVideos = async (offset = 0) => {
    try {
      if (offset === 0) {
        setLoading(true);
        setError(null);
      } else {
        setLoadingMore(true);
      }

      const params = {
        limit: 20,
        offset
      };

      if (searchQuery) {
        params.search = searchQuery;
      }

      const response = await videosAPI.getAll(params);
      const newVideos = response.data.videos;

      if (offset === 0) {
        setVideos(newVideos);
      } else {
        setVideos(prev => [...prev, ...newVideos]);
      }

      setHasMore(response.data.hasMore);
    } catch (err) {
      console.error('Erro ao carregar vídeos:', err);
      setError('Erro ao carregar vídeos. Verifique se o backend está funcionando.');
    } finally {
      setLoading(false);
      setLoadingMore(false);
    }
  };

  const handleLoadMore = () => {
    if (!loadingMore && hasMore) {
      loadVideos(videos.length);
    }
  };

  // Carregar downloads recentes
  const loadRecentDownloads = async () => {
    try {
      setDownloadsLoading(true);
      setDownloadsError(null);
      
      const response = await downloadsAPI.list(1, 6); // Últimos 6 downloads
      setDownloads(response.data.downloads || []);
    } catch (err) {
      console.error('Erro ao carregar downloads:', err);
      setDownloadsError('Erro ao carregar downloads recentes.');
    } finally {
      setDownloadsLoading(false);
    }
  };

  // Handler para remover download
  const handleDownloadDelete = (downloadId) => {
    setDownloads(prev => prev.filter(d => d.id !== downloadId));
  };

  return (
    <HomeContainer>
      {searchQuery && (
        <SearchInfo>
          <SearchTitle>
            Resultados da busca por: <SearchQuery>"{searchQuery}"</SearchQuery>
          </SearchTitle>
          {!loading && videos.length > 0 && (
            <p style={{ color: '#aaa', margin: 0 }}>
              {videos.length} vídeo{videos.length !== 1 ? 's' : ''} encontrado{videos.length !== 1 ? 's' : ''}
            </p>
          )}
        </SearchInfo>
      )}

      <VideoGrid 
        videos={videos} 
        loading={loading} 
        error={error} 
      />

      {hasMore && (
        <LoadMoreButton 
          onClick={handleLoadMore} 
          disabled={loadingMore}
        >
          {loadingMore ? 'Carregando...' : 'Carregar mais vídeos'}
        </LoadMoreButton>
      )}
    </HomeContainer>
  );
}

export default HomePage;
